import { Component } from '@angular/core';
import { BookService } from '../services/book.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-book-form',
  templateUrl: './book-form.component.html'
})
export class BookFormComponent {
  bookForm: FormGroup;
  message = '';

  constructor(private fb: FormBuilder, private bookService: BookService) {
    this.bookForm = this.fb.group({
      id: [0],
      title: ['', Validators.required],
      author: ['', Validators.required],
      price: ['', Validators.required],
      publisher: ['', Validators.required]
    });
  }

  saveBook() {
    if (this.bookForm.valid) {
      this.bookService.save(this.bookForm.value).subscribe((res: any) => {
        this.message = res.result?.message || 'Book saved successfully!';
        this.bookForm.reset({ id: 0 }); // Reset form with ID 0
      });
    }
  }
}
<h2>Add / Update Book</h2>

<form [formGroup]="bookForm" (ngSubmit)="saveBook()">
  <input type="hidden" formControlName="id" />

  <div>
    <label>Title:</label>
    <input type="text" formControlName="title" />
  </div>

  <div>
    <label>Author:</label>
    <input type="text" formControlName="author" />
  </div>

  <div>
    <label>Price:</label>
    <input type="number" formControlName="price" />
  </div>

  <div>
    <label>Publisher:</label>
    <input type="text" formControlName="publisher" />
  </div>

  <button type="submit">Save</button>
</form>

<p *ngIf="message">{{ message }}</p>
