import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent {

  form: any = {
    id: null,
    title: '',
    author: '',
    price: '',
    publisher: '',
    message: ''
  };

  fileToUpload: any = null;

  constructor(private http: HttpClient, private route: ActivatedRoute) {
    this.route.params.subscribe(params => {
      this.form.id = params['id'];
      if (this.form.id) {
        this.display();
      }
    });
  }

  ngOnInit(): void {}

  display() {
    this.http.get(`http://localhost:8081/Book/get/${this.form.id}`).subscribe((res: any) => {
      this.form = res.data || {};
      this.form.message = '';
    });
  }

  save() {
    this.http.post('http://localhost:8081/Book/save', this.form).subscribe((res: any) => {
      this.form.message = res.result?.message || 'Saved successfully';
      if (this.fileToUpload) {
        this.uploadFile();
      }
    });
  }

  onFileSelect(event: any) {
    this.fileToUpload = event.target.files.item(0);
  }

  uploadFile() {
    const formData = new FormData();
    formData.append('file', this.fileToUpload);

    this.http.post(`http://localhost:8081/Book/uploadCover/${this.form.id}`, formData).subscribe(
      (res: any) => {
        console.log('File uploaded:', res);
      },
      error => {
        console.error('Upload failed', error);
      }
    );
  }
}



<!-- Heading based on ID -->
<div *ngIf="form.id > 0">
  <h1>Update Book</h1>
</div>
<div *ngIf="form.id == null">
  <h1>Add Book</h1>
</div>

<!-- Message -->
<p style="color: green;">{{ form.message }}</p>

<!-- Book Form -->
<table>
  <tr>
    <th>Title</th>
    <td><input type="text" [(ngModel)]="form.title" /></td>
  </tr>

  <tr>
    <th>Author</th>
    <td><input type="text" [(ngModel)]="form.author" /></td>
  </tr>

  <tr>
    <th>Price</th>
    <td><input type="number" [(ngModel)]="form.price" /></td>
  </tr>

  <tr>
    <th>Publisher</th>
    <td><input type="text" [(ngModel)]="form.publisher" /></td>
  </tr>

  <tr>
    <th>Cover Preview:</th>
    <td>
      <img
        *ngIf="form.id"
        [src]="'http://localhost:8081/Book/cover/' + form.id"
        height="80px"
        width="60px"
        alt="No Cover"
      />
    </td>
  </tr>

  <tr>
    <th>Upload Cover:</th>
    <td>
      <input type="file" (change)="onFileSelect($event)" accept="image/*" />
    </td>
  </tr>

  <tr>
    <td></td>
    <td>
      <button (click)="save()" *ngIf="form.id == null">Add</button>
      <button (click)="save()" *ngIf="form.id > 0">Update</button>
    </td>
  </tr>
</table>
