import { Component } from '@angular/core';
import { BookService } from '../services/book.service';
@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit {
  bookList: any[] = [];
  pageNo: number = 0;
  searchCriteria: any = {
    title: '',
    author: '',
    publisher: ''
  };
  message = '';

  constructor(private bookService: BookService) {}

  ngOnInit(): void {
    this.searchBooks();
  }

  searchBooks() {
    this.bookService.search(this.searchCriteria, this.pageNo).subscribe((res: any) => {
      if (res.data) {
        this.bookList = res.data;
        this.message = '';
      } else {
        this.bookList = [];
        this.message = res.result?.message || 'No records found';
      }
    });
  }

  deleteBook(id: number) {
    this.bookService.delete([id]).subscribe((res: any) => {
      this.message = res.result?.message || 'Deleted';
      this.searchBooks(); // refresh list
    });
  }

  nextPage() {
    this.pageNo++;
    this.searchBooks();
  }

  previousPage() {
    if (this.pageNo > 0) {
      this.pageNo--;
      this.searchBooks();
    }
  }
}
